    private void txtSqlActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtSqlActionPerformed
        String sql = txtSql.getText();
        try{
            Class.forName("com.mysql.jdbc.Driver");
            String url="jdbc:mysql://localhost:3306/escuela";
            Connection cn = DriverManager.getConnection(url, "root", "mysql");

            Statement st = cn.createStatement();

            if(st.execute(sql)){
                    listar(st.getResultSet());
            }else{
                    txtSalida.setText("filas afectadas: "+st.getUpdateCount());
            }
            }catch(ClassNotFoundException ex){
                    txtSalida.setText("Error en el driver");
            } catch (SQLException e) {
                    txtSalida.setText("Error en la sentencia");
            }	
    }//GEN-LAST:event_txtSqlActionPerformed

