private void listar(ResultSet r) throws SQLException {

        ResultSetMetaData rmeta= r.getMetaData();//colecci√≥n metadata 
        int numColumnas= rmeta.getColumnCount();// Cantidad de campos 

        txtSalida.setText("");
        for(int i=1;i<=numColumnas;++i) 
                txtSalida.append(rmeta.getColumnName(i) + "    "); 

        txtSalida.append("\n ======================================\n"); 

        while(r.next()) { 
                for(int i=1;i<=numColumnas;++i) 
                        txtSalida.append(r.getString(i) + "    "); 
                txtSalida.append("\n"); 
        } 

} 


